import React, {useReducer} from 'react';
import {toDoReducer} from "./toDoReducer";

const ToDoApp = () => {
    const initialState = [{
        id: new Date().getTime(),
        todo: 'aprendiender react',
        done: false
    }];

    const [todDos] = useReducer(toDoReducer, initialState);


    return (
        <div>
            <h1> To Do App</h1>
            <hr />

            <ul>
                {
                  todDos.map( toDO => {
                      <li key={toDO.id} className='list-group-item'>
                          {toDO.description}
                      </li>
                  })
                }
            </ul>
        </div>
    );
};

export default ToDoApp;
