import React, {useState} from 'react';
import './counter.css';

const CounterApp = () => {

    const [{counter_1, counter_2}, {setCounter}] = useState({
        counter_1: 10,
        counter_2: 20
    });

    return (
        <div>
            <h1>Counter {counter_1}</h1>
            <h1>Counter {counter_2}</h1>
            <hr/>

            <button
                className="btn btn-primary"
                onClick={ () => {
                    setCounter( counter_1 + 1);
                }}
            >
                +1
            </button>

        </div>
    );
};

export default CounterApp;
