import React, {useReducer} from 'react';
import {toDoReducer} from "./toDoReducer";

const ToDoApp = () => {
    const initialState = [{
        id: new Date().getTime(),
        description: 'aprendiender react',
        done: false
    }];

    const [todDos] = useReducer(toDoReducer, initialState);

    console.log(todDos)


    return (
        <div>
            <h1> ToDos App ({todDos.length })</h1>
            <hr />

            <ul className='list-group list-group-flush'>
                {
                  todDos.map( (toDO, i ) => (
                      <li key={toDO.id} className='list-group-item'>
                         <p className='text'> {i+1}. {toDO.description} </p>
                      </li>
                  ))
                }
            </ul>
        </div>
    );
};

export default ToDoApp;
