export const toDoReducer = (state = [], action) => {
    switch (action.type) {
        case 'add':
            return [...state, action.payload];
        case 'delete':
            return state.filter( todos =>  todos.id !== state.payload );

        case 'toggle':
            return state.map( todo =>
                ( todo.id === action.payload )
                    ? { ...todo, done: !todo.done }
                    : todo
            );

        default:
            return state;
    }
}
